import React, { useState } from 'react';
import LargComponent from './Components/ItemsCollection/LargeComponent';
import NewItemAdded from './Components/Form/NewItemAdded';
//import {chosenYear} from './Components/ItemsCollection/LargeComponent';

const DataItems = [
  { id : 'e1' ,
    date : new Date (2022,2,26) ,
    title : 'Oven' ,
    price : 150 },

  { id : 'e2' ,
    date : new Date (2022,4,15) ,
    title : 'Hand Blender' ,
    price : 45},

  { id : 'e3' ,
    date : new Date (2022,6,8) ,
    title : 'Fridge' ,
    price : 500 },

  { id : 'e4' ,
    date : new Date (2022,11,14) ,
    title : 'Fan' ,
    price : 70},

  { id : 'e5',
    date : new Date (2022,9,7) ,
    title : 'Microwave' ,
    price : 200}
  ];
const FilteredItems = [...DataItems.filter((item)=>
  item.date.getFullYear()=== '2020')]; //Defining the filter data with methods
