function App() {
  const [items, setitems] = useState(DataItems); //for setting Data will always be used
  //const [Items , setItems]= useState(DataItems); //for setting Data after filteration

  const SavingItemHandler =(Data)=> {
    setitems((prevItems)=> {
      return ([Data, ...prevItems]);
    });
  };
  const ApplyFilter =()=>{
    setitems (FilteredItems);
  };
  return (
    <div>
      <h2> Hello Shamousa Let's Goo </h2>
      <NewItemAdded onSave={SavingItemHandler} />
      <LargComponent items = {items} filter={ApplyFilter}></LargComponent>
    </div>
  );
}
export default App;
