import React , {useState} from 'react';
import Card from '../MainCard/Card';
import Component1 from '../Item/Component1';
import './LargeComponent.css';
import ChoseYear from '../YearFilterComponent/Filter';

function LargeComponent(props){
    const [chosenYear,setChosenYear] =useState('2022');

    const FilterHandle =(SelectedYear)=> {
        setChosenYear(SelectedYear);
        /*Reciving a chosen year (target.value) ... and ...  Setting it as current year in the component i(shown drop down)*/
    }
    ;
    return(
        <Card className="LargeComponent">
            <ChoseYear onChangeYear={FilterHandle} send={chosenYear}/>
            <Component1 
                date = {props.items[0].date}
                title = {props.items[0].title}
                price = {props.items[0].price}/>
            <Component1 
                date = {props.items[1].date}
                title = {props.items[1].title}
                price = {props.items[1].price}/>
            <Component1 
                date = {props.items[2].date}
                title = {props.items[2].title}
                price = {props.items[2].price}/>
            <Component1 
                date = {props.items[3].date}
                title = {props.items[3].title}
                price = {props.items[3].price}/>
            <Component1 
                date = {props.items[4].date}
                title = {props.items[4].title}
                price = {props.items[4].price}/>
        </Card>
    );
}

export default LargeComponent;
