        /*Reciving a chosen year (target.value) ... and ...  Setting it as current year in the component itself (shown drop down)*/
    };
    return(
        <Card className="LargeComponent">
            <ChoseYear onChangeYear={FilterHandle} /*onFilter={ApplyFilter}*/ send={chosenYear}/>
            <div> {items.map( (item)=> 
                (<Component1 
                key = {item.id}
                date = {item.date}
                title = {item.title}
                price = {item.price}/>))}
            </div>

            {/*<div> {props.items.map( (item)=> 
                (<Component1 
                key = {item.id}
                date = {item.date}
                title = {item.title}
                price = {item.price}/>))}
            </div>/*}   


            {/*<div>{props.items.filter((item)=>
                item.date.include(chosenYear)
                (<Component1
                key = {item.id}
                date = {item.date}
                title = {item.title}
