import React, { useState } from "react";
import './ItemForm.css';

const ItemForm =(props)=> {
    const [intialTitle,NewTitle] = useState(props.title);
    const [intialPrice,NewPrice] = useState(props.price);
    const [intialDate,NewDate] = useState(props.date);

    const OnChangeTitleHandler =(event)=> {
        NewTitle (event.target.value);
    }

    const OnChangePriceHandler =(event)=> {
        NewPrice (event.target.value);
    }

    const OnChangeDateHandler =(event)=> {
        NewDate (event.target.value);
    }
    const OnSubmitHandler =(event)=> {
        event.Pre
    }
    

    return (
        <form onSubmit={OnSubmitHandler}>
            <h2>Add Item!</h2>
            <div className="New">
                <label>Title is :</label>
                <input type='text' onChange={OnChangeTitleHandler}/>
            </div>
            <div className="New">
                <label>Price is :</label>
                <input type='number' min='5' step='0.1' onChange={OnChangePriceHandler} />
            </div>
            <div className="New">
                <label>Date is :</label>
                <input type='date' min='2018-01-01' max ='2023-12-31' onChange={OnChangeDateHandler}/>
            </div>
            <div>
                <button className="DoneButton" type="submit">Done!</button>
            </div>
        </form>
    )
}
export default ItemForm;
