import React, { useState } from "react";
import './ItemForm.css';

const ItemForm =(props)=> {
    const [OldTitle,NewTitle] = useState(props);
    const [OldPrice,NewPrice] = useState(props.price);
    const [OldDate,NewDate] = useState(props.date);

    const OnChangeTitleHandler =(event)=> {
        NewTitle (event.target.value);
    }
    const OnChangePriceHandler =(event)=> {
        NewPrice (event.target.value);
    }
    const OnChangeDateHandler =(event)=> {
        NewDate (event.target.value);
    }

    const OnSubmitHandler =(event)=> {
        event.preventDefault();
        const NewData = {
            title : OldTitle,
            price : OldPrice,
            date : new Date (OldDate)
        };
        props.SaveDataHandler(NewData);
        NewTitle ('');
        NewPrice ('');
        NewDate ('');
    };
    

    return (
        <form onSubmit={OnSubmitHandler}>
            <h2>Add Item!</h2>
            <div className="New">
                <label>Title is :</label>
                <input type='text'  value={OldTitle} onChange={OnChangeTitleHandler}/>
            </div>
            <div className="New">
                <label>Price is :</label>
                <input type='number' min='5' step='0.1' value={OldPrice} onChange={OnChangePriceHandler} />
            </div>
            <div className="New">
                <label>Date is :</label>
                <input type='date' min='2018-01-01' max ='2023-12-31' value={OldDate} onChange={OnChangeDateHandler}/>
            </div>
            <div>
                <button className="DoneButton" type="submit">Done!</button>
            </div>
        </form>
    )
}
export default ItemForm;
