import React , {useState} from "react";
import './YearFilter.css';
import Card from "../MainCard/Card";
import Result from "./Initializing";
import Component1 from "../Item/Component1";
import LargeComponent from "../ItemsCollection/LargeComponent";

const FilterYear = (props) => {
    const [year,newYear] = useState ('');
    const OnFilterHandler =(event) => {
        event.preventDefault();
        const value = event.target.value;
        newYear(value);
    };
    
    const Handler1 = ()=>{
        items = props.items[3];
    };
    const Handler2 = ()=>{
        items = props.items[2];
    };
    const Handler3 = ()=>{
        items = props.items[1];
    };
    const Handler4 = ()=>{
        items = props.items[0];
    };
    return (
        <div>
            <div className="FilterCard">
                <Card>
                    <div className="Filter">
                        <label>Filter By Year</label>
                        <select onChange={OnFilterHandler}>
                            <option >Choose a Year</option>
                            <option value="2019" onClick={Handler1}>2019</option>
                            <option value="2020" onClick={Handler2}>2020</option>
                            <option value="2021" onClick={Handler3}>2021</option>
                            <option value="2022" onClick={Handler4}>2022</option>
                        </select>
                    </div>
                </Card>
                <Card>
                    <Result year={year} items={props.items}/>
                </Card>
            </div>
        </div>
    );
};
export default FilterYear;
